function isAuth(hash) {
  var authsucc = false;
  cfg = config.readConfig();
  if (cfg.auth == false) {
    authsucc = true;
  } else {
    if (typeof (hash) !== "undefined" && hash != "" && hash == crypto.createHash('md5').update(cfg["owner-password"]).digest('hex')) {
      authsucc = true;
    }
  }
  return authsucc;
}

