router.use(function (req, res, next) {
  if (req["_parsedUrl"]["pathname"] != "/icon") {
    additional.showRequestInLogs(req, res);
  }
  cfg = config.readConfig();
  ip = req.headers["x-forwarded-for"] || req.socket.remoteAddress;
  ip = ip.replace("::ffff:", "").replace("::1", "127.0.0.1");
  if (cfg["internet-access"] == false && ip != "127.0.0.1") {
    res.send("Cannot be accessed from the internet");
  } else {
    authsucc = auth_manager.authorize(
      req.cookies["kbk__hash"],
      req.cookies["kbk__login"]
    );
    if (authsucc == true) {
      next();
    } else {
      res.redirect("/login.html");
    }
  }
});

router.get("/icon", function (req, res) {
  if (typeof req.query.server !== "undefined" && additional.validatePath(req.query.server) == true) {
    if (fs.existsSync("./servers/" + req.query.server + "/server-icon.png")) {
      res.sendFile("servers/" + req.query.server + "/server-icon.png", {
        root: "./",
      });
    } else {
      var img = Buffer.from(
        "",
        "base64"
      );

      res.writeHead(200, {
        "Content-Type": "image/png",
        "Content-Length": img.length,
      });
      res.end(img);
    }
  } else {
    res.send("");
  }
});

router.get("/completion", function (req, res) {
  if(additional.validatePath(req.query.server) != true){
    res.status(403).send();
    return;
  }
  fs.writeFileSync("./servers/" + req.query.server + "/eula.txt", "eula=true");
  newForgeInstalling = false;
  jfn = req.query.jf;
  if (jfn.match(/forge/gim) != null) {
    version = jfn.match(/1\.\d+\.\d+/gim);
    if (version != null) {
      version = version[0];
      version_index = version.split(".")[1];
      if (version_index > 16) {
        newForgeInstalling = true;
      }
    }
  }
  if (process.platform == "win32") {
    if (newForgeInstalling == true) {
      run_read = fs
        .readFileSync("./servers/" + req.query.server + "/run.bat")
        .toString();
      run_read = run_read.split("\n");
      jl = null;
      run_read.forEach(function (line) {
        if (line.match(/java/gm) != null) {
          jl = line;
        }
      });
      if (jl != null) {
        args_path = jl
          .split(" ")
          .slice(1)
          .join(" ")
          .replace(/\\r/gim, "")
          .trim();
        fs.writeFileSync(
          "./servers/" + req.query.server + "/start.bat",
          "@echo off\nchcp 65001>nul\ncd servers\ncd " +
            req.query.server +
            "\n" +
            Buffer.from(req.query.startcmd, "base64")
              .toString()
              .replace("-jar", "")
              .trim() +
            " " +
            args_path +
            " nogui"
        );
      }
    } else {
      fs.writeFileSync(
        "./servers/" + req.query.server + "/start.bat",
        "@echo off\nchcp 65001>nul\ncd servers\ncd " +
          req.query.server +
          "\n" +
          Buffer.from(req.query.startcmd, "base64") +
          " " +
          req.query.jf +
          " nogui"
      );
    }
  } else if (process.platform == "linux") {
    if (newForgeInstalling == true) {
      run_read = fs
        .readFileSync("./servers/" + req.query.server + "/run.sh")
        .toString();
      run_read = run_read.split("\n");
      jl = null;
      run_read.forEach(function (line) {
        if (line.match(/java/gm) != null) {
          jl = line;
        }
      });
      if (jl != null) {
        args_path = jl
          .split(" ")
          .slice(1)
          .join(" ")
          .replace(/\\r/gim, "")
          .trim();
        fs.writeFileSync(
          "./servers/" + req.query.server + "/start.sh",
          "cd servers\ncd " +
            req.query.server +
            "\n" +
            Buffer.from(req.query.startcmd, "base64")
              .toString()
              .replace("-jar", "")
              .trim() +
            " " +
            args_path +
            " nogui"
        );
      }
    } else {
      fs.writeFileSync(
        "./servers/" + req.query.server + "/start.sh",
        "cd servers\ncd " +
          req.query.server +
          "\n" +
          Buffer.from(req.query.startcmd, "base64") +
          " " +
          req.query.jf +
          " nogui"
      );
    }
  } else {
    console.log(
      colors.red(
        additional.getTimeFormatted() +
          " " +
          process.platform +
          " not supported"
      )
    );
  }
  fs.writeFileSync(
    "./servers/" + req.query.server + "/server.properties",
    "server-port=" +
      req.query.port +
      "\nquery.port=" +
      req.query.port +
      "\nenable-query=true\nonline-mode=" +
      req.query.onMode +
      "\nmotd=\u00A7f" +
      req.query.server + "\u00A7bCreated with Kubek"
  );
  if (fs.existsSync("./servers/servers.json")) {
    cge = JSON.parse(fs.readFileSync("./servers/servers.json"));
  } else {
    cge = {};
  }
  servers_logs[req.query.server] = "";
  servers_instances[req.query.server] = "";
  sss = {
    status: "stopped",
    restartOnError: true,
    stopCommand: "stop",
    restartScheduler: {
      enabled: "false",
      crontab: "* * * * *",
    },
  };
  cge[req.query.server] = sss;
  serverjson_cfg = cge;
  config.writeServersJSON(cge);
  res.send("Success");
});

