router.get("/checkBEVersion", function (req, res) {
  bever = req.query.version;
  jsn = {
    exists: false,
    url: "",
  };
  synt_check = false;

  split = bever.split(".");
  split.forEach(function (v, i) {
    v = parseInt(v);
    if (v != NaN) {
      split[i] = v;
    } else {
      split[i] = -1;
    }
  });
  if (split[0] == 1 || split[0] == 0) {
    if (split[1] > -1 && split[2] > -1 && split[3] > -1) {
      synt_check = true;
    }
  }
  if (synt_check == true) {
    if (process.platform == "win32") {
      chk_url =
        "https://minecraft.azureedge.net/bin-win/bedrock-server-" +
        bever +
        ".zip";
    } else if (process.platform == "linux") {
      chk_url =
        "https://minecraft.azureedge.net/bin-linux/bedrock-server-" +
        bever +
        ".zip";
    } else {
      return;
    }

    checkBEVersionByURL(chk_url, function (result) {
      jsn["exists"] = result;
      if (result == true) {
        jsn["url"] = chk_url;
      }
      res.send(jsn);
    });
  } else {
    res.json(jsn);
  }
});

function checkBEVersionByURL(url, cb) {
  request(url, function (err, resp) {
    if (resp.statusCode === 200) {
      return cb(true);
    }

    return cb(false);
  });
}

