router.get("/delete", function (req, res) {
  if(additional.validatePath(req.query.server) != true){
    res.status(403).send();
    return;
  }
  perms = auth_manager.getUserPermissions(req);
  if (perms.includes(ACCESS_PERMISSION)) {
    if (typeof serverjson_cfg[req.query.server] !== "undefined") {
      delete serverjson_cfg[req.query.server];
      config.writeServersJSON(serverjson_cfg);
      setTimeout(function () {
        fs.rm(
          "./servers/" + req.query.server,
          {
            recursive: true,
            force: true,
          },
          function () {
            res.send("true");
          }
        );
      }, 500);
    } else {
      res.send("false");
    }
  } else {
    res.status(403).send();
  }
});

