router.get("/restart", function (req, res) {
  if (
    typeof serverjson_cfg[req.query.server] !== "undefined" &&
    serverjson_cfg[req.query.server].status == "started" &&
    typeof restart_after_stop[req.query.server] == "undefined"
  ) {
    restart_after_stop[req.query.server] = true;
    command = "stop";
    command = Buffer.from(command, "utf-8").toString();
    servers_logs[req.query.server] =
      servers_logs[req.query.server] + command + "\n";
    servers_instances[req.query.server].stdin.write(command + "\n");
    res.send("true");
  } else {
    res.send("false");
  }
});

